(function(){window.onload=function(){var t=new e("calculadora");t.criarBtsNumeros(10);t.criarBtsOperadores("+","-","*","/","=");t.ativarBotoes()};var e=function(e){var t="";var n=document.getElementById(e);this.criandoVisor=function(){var e=document.createElement("div");e.id="visor";e.innerHTML="Calculadora inteligente";n.appendChild(e)};this.criarBtsNumeros=function(e){n.innerHTML+='<ul id="numeros"></ul>';for(i=0;i<e;i++){n.getElementsByTagName("ul").item(0).innerHTML+='<li name="bt">'+i+"</li>"}};this.criarBtsOperadores=function(){n.innerHTML+='<ul id="operadores"></ul>';for(i=0;i<arguments.length;i++){n.getElementsByTagName("ul").item(1).innerHTML+='<li name="bt">'+arguments[i]+"</li>"}};this.ativarBotoes=function(){for(i=0;i<10;i++){document.getElementsByTagName("li").item(i).onclick=function(){t=t+this.innerHTML;document.getElementById("visor").innerHTML=t}}}}})();